def list_strong_exercises(strong_filename):
    with open(strong_filename, mode='r') as strong_csv:
        strong_reader = csv.DictReader(strong_csv, delimiter=';')
        strong_reader_headers = strong_reader.fieldnames
        strong_exercise_list = []
        for row in strong_reader:
            strong_exercise_list.append(row[strong_reader_headers[2]])
        filtered_strong_exercises = [*set(strong_exercise_list)]
        filtered_strong_exercises.sort()
        print(filtered_strong_exercises)
        print('\n')

def list_jefit_exercises(jefit_filename):
    with open(jefit_filename, mode='r') as jefit_csv:
        jefit_reader = csv.DictReader(jefit_csv, delimiter=',')
        jefit_reader_headers = jefit_reader.fieldnames
        jefit_exercise_list = []
        for row in jefit_reader:
            jefit_exercise_list.append(row[jefit_reader_headers[9]])
        filtered_jefit_exercises = [*set(jefit_exercise_list)]
        filtered_jefit_exercises.sort()
        print(filtered_jefit_exercises)
        print('\n')